module Compressor7_2(A1,A2,A3,A4,A5,A6,A7,Cin1,Cin2,Cout1,Cout2,SUM, CARRY);
	input A1,A2,A3,A4,A5,A6,A7;
	input Cin1,Cin2;
	output Cout1,Cout2;
	output SUM, CARRY;
	wire sum1,sum2,sum3;
	wire c1,c2,c3;
	fulladder fd1 (A1,A2,A3,sum1,c1);
	fulladder fd2 (A4,A5,A6,sum2,c2);
	fulladder fd3 (sum1,sum2,A7,sum3,c3);
	fulladder fd4 (c1,c2,c3,Cout1,Cout2);
	fulladder fd5 (sum3,Cin1,Cin2,SUM,CARRY);
endmodule 



